__ht__function(g, rect, comp, data, view) {
var rectWidth = rect.width;
var rectHeight = rect.height;
var remainingPower = comp.getValue('remainingPower');
var lineWidth = comp.getValue('lineWidth');
var batteryColor = comp.getColor('batteryColor');

var rootWidth = rectWidth / 120;
var roundWidth = rootWidth * 4;
var headWidth = lineWidth * 3;
var headHeight = rectHeight / 3;

// 保存绘制前状态
g.save();

// 裁切多余部分
g.beginPath();
g.rect(0, 0, rectWidth, rectHeight);
g.clip();

// 电量背景
g.save();
g.beginPath();
g.lineWidth = lineWidth;
g.fillStyle = '#E6EAEE';
ht.Default.drawRoundRect(
    g,
    lineWidth * 2,
    lineWidth * 2,
    rectWidth - lineWidth * 6,
    rectHeight - lineWidth * 4,
    roundWidth / 2
);
g.fill();
g.clip();

// 剩余电量
g.beginPath();
g.lineWidth = lineWidth;
g.fillStyle = batteryColor;
ht.Default.drawRoundRect(
    g,
    lineWidth * 2,
    lineWidth * 2,
    (rectWidth - lineWidth * 6) * remainingPower,
    rectHeight - lineWidth * 4,
    roundWidth / 2
);
g.fill();
g.restore();

// 绘制外围圆角矩形
g.beginPath();
g.lineWidth = lineWidth;
g.strokeStyle = '#E6EAEE';
ht.Default.drawRoundRect(
    g,
    lineWidth / 2,
    lineWidth / 2,
    rectWidth - lineWidth * 3,
    rectHeight - lineWidth,
    roundWidth
);
g.stroke();

// 绘制外围电池头
g.beginPath();
g.fillStyle = '#E6EAEE';
g.fillRect(
    rectWidth - headWidth,
    rectHeight / 2 - headHeight / 2,
    headWidth,
    headHeight
);

// 绘制闪电
// 左上部分
g.fillStyle = '#6A707B';
g.beginPath();
g.moveTo(rectWidth / 2 + rootWidth * 3, rectHeight / 4);
g.lineTo(rectWidth / 2 - rootWidth * 5, rectHeight / 2 + rootWidth * 2);
g.lineTo(rectWidth / 2, rectHeight / 2 + rootWidth * 2);
g.fill();
// 右下部分
g.beginPath();
g.translate(rectWidth, rectHeight);
g.rotate(Math.PI);
g.moveTo(rectWidth / 2 + rootWidth * 3, rectHeight / 4);
g.lineTo(rectWidth / 2 - rootWidth * 5, rectHeight / 2 + rootWidth * 2);
g.lineTo(rectWidth / 2, rectHeight / 2 + rootWidth * 2);
g.fill();

// 恢复绘制前的状态
g.restore();
}
