__ht__function(g, rect, comp, data, view) {
// * 获取四舍五入的两位小数
function getTwoDecimalPoints(num) {
    return parseFloat(num.toFixed(2));
}

// * 获取并定义变量
var rectWidth = rect.width;
var rectHeight = rect.height;
var width = rectWidth > rectHeight ? rectHeight : rectWidth;
var posX = rectWidth / 2;
var posY = rectHeight / 2;
var blankCirclePercent = comp.getValue('blankCirclePercent');
var progressPercentage = comp.getValue('progressPercentage');
var progressColor = comp.getValue('progressColor');
var innerWidth = width * blankCirclePercent;
var lineGap = getTwoDecimalPoints((2 / 252) * width);
var proAngleStart = (Math.PI / 2) * 3;
var percentageAndColor = progressPercentage.map(function(item, index) {
    var color = progressColor.filter(function(item, idx) {
        return index === idx;
    })[0];
    return {
        percentage: item,
        color: color
    };
});
var lineWidth = (width / 2 - innerWidth / 2 - (percentageAndColor.length - 1) * lineGap) / percentageAndColor.length;

// * 裁切
g.beginPath();
g.rect(0, 0, rectWidth, rectHeight);
g.clip();

// * 绘制
function drawProgress(index, strokeStyle, start, end, isProgress) {
    g.save();
    g.beginPath();
    g.arc(
        posX,
        posY,
        innerWidth / 2 + (lineWidth + lineGap) * index + lineWidth / 2,
        start,
        end
    );
    if (!isProgress) g.globalAlpha = 0.2;
    g.lineWidth = lineWidth;
    g.strokeStyle = strokeStyle;
    g.lineCap = 'round';
    g.stroke();
    g.restore();
}

percentageAndColor.forEach(function(item, index) {
    var start = proAngleStart;
    var end = proAngleStart + ((Math.PI * 2) / 100) * item.percentage;
    drawProgress(index, item.color, 0, Math.PI * 2, false);
    drawProgress(index, item.color, start, end, true);
});
}
