__ht__function(g, rect, comp, data, view) {
var color = comp.getColor('color');
if (!color) {
    return;
}
var cx = rect.x + rect.width / 2;
var cy = rect.y + rect.height / 2;
var theta = 0;
var r = Math.min(rect.width, rect.height)/2 * comp.getValue('radius');

g.strokeStyle = color;
for (var i = 0; i < 60; i++) {
    g.beginPath();
    g.arc(
        cx + Math.cos(theta) * r, 
        cy + Math.sin(theta) * r, 
        i % 5 === 0 ? 2 : 1, 
        0, Math.PI * 2, true);
    g.closePath();
    g.lineWidth = comp.getValue('thickness');
    g.stroke();
    theta = theta + (6 * Math.PI / 180);
}
}
