__ht__function(g, rect, comp, data, view) {
var fontColor = comp.getValue('lineColor');
var lineColor = comp.getValue('fontColor');

g.lineWidth = 1;
g.lineCap = 'butt';
g.strokeStyle = lineColor;
g.fillStyle = lineColor;
g.save();

var items = comp.getValue('items');

var width = rect.width;
var height = rect.height;

//等分数
var sp = items.length * 3 + 1;
var spWidth = width / sp;


//水平轴
g.beginPath();
g.moveTo(0,height);

g.lineTo(width,height);
g.stroke();

g.textAlign = 'center';
g.textBaseline = 'middle';
g.font = comp.getValue('fontStyle');;
g.fillStyle = true;

g.strokeStyle = fontColor;
g.fillStyle = fontColor;

//对象均分
for(var i=0;i<items.length;i++){
    var item = items[i];
    g.fillText(item, spWidth*2+i*3*spWidth, height + 8);
}

g.restore();
}
