__ht__function(g, rect, comp, data, view) {
var fontColor = comp.getValue('fontColor');
var lineColor = comp.getValue('lineColor');

g.lineWidth = 1;
g.lineCap = 'butt';
g.strokeStyle = lineColor;
g.fillStyle = lineColor;
g.save();

var height = rect.height;
var width = rect.width;

var scale = comp.getValue('scale');
var lineLength = comp.getValue('lineLength');
var pointWidth = comp.getValue('pointWidth');
var maxValue = comp.getValue('maxValue');

//是否标度最大值
var isMaxShow = comp.getValue('isMaxShow');
//刻度线是否绘制
var isScaleShow = comp.getValue('isScaleShow');
var isShade = comp.getValue('isShade');
var shadeColors = comp.getValue('shadeColors');

//是否绘制长刻度线
var isScaleLineShow = comp.getValue('isScaleLineShow');

//格式化处理
var format = comp.getValue('format');
if(format==undefined){
    format = '';
}

g.beginPath();
g.moveTo(0,0);
g.lineTo(0,height);
g.stroke();

g.textAlign = 'right';
g.textBaseline = 'middle';
g.font = comp.getValue('fontStyle');;
g.fillStyle = true;

var splitSize = comp.getValue('splitSize');
if(splitSize>0){

    for(var i=0;i<=splitSize;i++){

        var activeScale = parseInt(maxValue/splitSize) * i;
        var yPoint = height - activeScale / maxValue * height;

        if(yPoint!=height && (isMaxShow || yPoint!=0)){

            g.strokeStyle = lineColor;
            g.fillStyle = lineColor;

            if(isScaleShow){
                //绘制标度
                g.beginPath();
                g.moveTo(0,yPoint);
                g.lineTo(pointWidth,yPoint);
                g.stroke();
            }
        }

        if(yPoint!=height && (isMaxShow || yPoint!=0)){
            g.strokeStyle = lineColor;
            g.fillStyle = lineColor;

            if(isScaleLineShow && yPoint!=0){
                //绘制行线
                g.beginPath();
                g.moveTo(0,yPoint);
                g.lineTo(width,yPoint);
                g.stroke();
            }
        }

        //绘制数字
        if(isMaxShow || yPoint!=0){
            
            g.strokeStyle = fontColor;
            g.fillStyle = fontColor;

            if(format.indexOf('{value}')>=0){
                g.fillText(format.replace('{value}',activeScale), -2, yPoint);
            }else{
                g.fillText(activeScale, -2, yPoint);
            }

        }

        if(isShade){

            if(i!=0){
                g.beginPath();
                g.strokeStyle = shadeColors[i % shadeColors.length];
                g.fillStyle = shadeColors[i % shadeColors.length];
                g.rect(0,yPoint,width,height/splitSize);
                g.fill();
            }

        }
    }

}else{

    for(var i=0;i<scale.length;i++){

        var activeScale = scale[i];
        var yPoint = height - activeScale / maxValue * height;

        //绘制标度
        if(yPoint!=height &&(isMaxShow || yPoint!=0)){

            g.strokeStyle = lineColor;
            g.fillStyle = lineColor;

            if(isScaleShow){
                g.beginPath();
                g.moveTo(0,yPoint);
                g.lineTo(pointWidth,yPoint);
                g.stroke();
            }

        }

        //绘制数字
        if(isMaxShow || yPoint!=0){

            g.strokeStyle = fontColor;
            g.fillStyle = fontColor;

            if(format.indexOf('{value}')>=0){
                g.fillText(format.replace('{value}',activeScale), -2, yPoint);
            }else{
                g.fillText(activeScale, -2, yPoint);
            }

           
        }

    }

}

g.restore();
}
