__ht__function(g, rect, comp, data, view) {
var colors = comp.getValue('colors');
var datas = comp.getValue('datas');
var maxValue = comp.getValue('maxValue');

var shadeColors = comp.getValue('shadeColors');

var isPoint = comp.getValue('isPoint');
var isShade = comp.getValue('isShade');

var pointRs = comp.getValue('pointRs');
var circleAngle = {
    sAngle: 0,
    eAngle: 2 * Math.PI
};


g.lineWidth = comp.getValue('lineWidth');
g.lineCap = 'butt';

var width = rect.width;
var height = rect.height;

g.save();

//多线绘制
for(var i=0;i<datas.length;i++){

    var color = colors[i % colors.length];
    var data = datas[i];

    g.strokeStyle = color;
    g.fillStyle = color;

    g.beginPath();

    for(var j=0;j<data.length;j++){
        var fina = data[j];
        if(j==0){
            g.moveTo(j*(width/(data.length-1)),height*(1-fina/maxValue));
        }else{
            g.lineTo(j*(width/(data.length-1)),height*(1-fina/maxValue));
        }
    }

    g.stroke();

}

//点绘制
if(isPoint){

    for(var i=0;i<datas.length;i++){

        var color = colors[i % colors.length];
        var data = datas[i];

        g.strokeStyle = color;
        g.fillStyle = color;

        for(var j=0;j<data.length;j++){
            var fina = data[j];
            g.beginPath();
            g.arc(j*(width/(data.length-1)), height*(1-fina/maxValue), pointRs, circleAngle.sAngle, circleAngle.eAngle);
            g.closePath();
            g.fill();
        }

    }

}

//底纹绘制
if(isShade){

    for(var i=0;i<datas.length;i++){

        var color = shadeColors[i % shadeColors.length];
        var data = datas[i];

        g.strokeStyle = color;
        g.fillStyle = color;

        /*var crd = g.createLinearGradient(0,0,0,100);
        crd.addColorStop(0,color);
        crd.addColorStop(1,'rgba(12,16,41,0.2)');
        g.fillStyle = crd;*/

        g.beginPath();

        g.moveTo(0,height);

        for(var j=0;j<data.length;j++){
            var fina = data[j];
            g.lineTo(j*(width/(data.length-1)),height*(1-fina/maxValue));        
        }

        g.lineTo(width,height);

        g.closePath();

        g.fill();

    }

}

g.restore();
}
