__ht__function(g, rect, comp, data, view) {
var x = rect.x;
var y = rect.y;
var width = rect.width;
var height = rect.height;
var state = comp.getValue('state');
var left = comp.getValue('left');
var right = comp.getValue('right');

// 绘制开关区域
var sHeight = height / 2;
var min = width > sHeight ? sHeight : width;
g.save();
g.shadowBlur = 18;
g.shadowColor = 'rgba(0, 0, 0, 0.5)';
g.beginPath();
g.moveTo(x + width / 4, y);
g.lineTo(x + width * 3 / 4, y);
g.bezierCurveTo(x + width, y, x + width, y + sHeight, x + width * 3 / 4, y + sHeight);
g.lineTo(x + width / 4, y + sHeight);
g.bezierCurveTo(x, y + sHeight, x, y, x + width / 4, y);
g.fillStyle = '#fafafa';
g.fill();
g.restore();

// 设置字体样式和绘制分隔符
var str = min * 40 / 100 + 'px Arial';
g.font = str;
g.fillStyle = '#cccccc';
g.textAlign = 'center';
g.textBaseline = 'middle';
g.fillText('/', x + width / 2, y + height * 3 / 4);

// 分别绘制两种状态下的开关状态
var r = min * 5 / 40;
if (state) {
    g.beginPath();
    g.fillStyle = 'red';
    g.arc(x + width / 4, y + sHeight / 2, r, 0, Math.PI * 2);
    g.fill();
    g.textAlign = 'right';
    g.fillText(left + '  ', x + width / 2, y + height * 3 / 4);

    g.beginPath();
    g.fillStyle = '#cccccc';
    g.arc(x + width * 3 / 4, y + sHeight / 2, r, 0, Math.PI * 2);
    g.fill();
    g.textAlign = 'left';
    g.fillText('  ' + right, x + width / 2, y + height * 3 / 4);
} else {
    g.beginPath();
    g.fillStyle = '#cccccc';
    g.arc(x + width / 4, y + sHeight / 2, r, 0, Math.PI * 2);
    g.fill();
    g.textAlign = 'right';
    g.fillText(left + '  ', x + width / 2, y + height * 3 / 4);

    g.beginPath();
    g.fillStyle = 'blue';
    g.arc(x + width * 3 / 4, y + sHeight / 2, r, 0, Math.PI * 2);
    g.fill();
    g.textAlign = 'left';
    g.fillText('  ' + right, x + width / 2, y + height * 3 / 4);
}
}
