__ht__function(g, rect, comp, data, view) {
var x = rect.x;
var y = rect.y;
var width = rect.width; 
var height = rect.height;
var category = comp.getValue('category');
var num = comp.getValue('num');
var sign = comp.getValue('sign');
var min = width > height ? height : width;

// 绘制背景
g.save();
g.beginPath();
g.moveTo(x + width / 10, y);
g.lineTo(x + width * 9 / 10, y);
g.quadraticCurveTo(x + width, y, x + width, y + width / 10);
g.lineTo(x + width, y + height - width /10);
g.quadraticCurveTo(x + width, y + height, x + width * 9 / 10, y + height);
g.lineTo(x + width / 10, y + height);
g.quadraticCurveTo(x, y + height, x, y + height - width / 10);
g.lineTo(x, y + width / 10);
g.quadraticCurveTo(x, y, x + width / 10, y);
g.clip();
g.fillStyle = '#333333';
g.fillRect(x, y, width, height);

// 进行数值判断，是否超出最大计量数
if (num > 999999) {
    g.fillStyle = 'red';
    g.textAlign = 'center';
    g.textBaseline = 'middle';
    g.fillText('数值超出最大计量数', width / 2 + x, height / 2 + y);
    g.restore();
    return ;
}

// 取输入数值各位数
var list = [];
for(var i = 5; i > -1; i --) {
    list[i] = num % 10;
    num =parseInt(num / 10);   
}

// 回执数字区域块
g.textAlign = 'center';
g.textBaseline = 'middle';
str = min  * 20 / 30 + 'px Arial' ;
g.font = str;
var rectX = x;
var rectY = y;
for( var i = 0; i < 6; i ++){   
    if (category) {     // 带符号位的绘制
        if (i == 3) {
            g.fillStyle = '#383838';
            g.fillRect(rectX, rectY, width * 2 / 38, height);
            g.fillStyle = 'white';
            g.fillText(sign, rectX + width * 1 / 38, rectY + height / 2);
            rectX += width * 3 / 38;
            g.fillStyle = '#383838';
            g.fillRect(rectX, rectY, width * 5 / 38, height);
            g.fillStyle = 'white';
            g.fillText(list[i], rectX + width * 5 / 76, rectY + height / 2);
            rectX += width * 6 / 38;
        } else {
            g.fillStyle = '#383838';
            g.fillRect(rectX, rectY, width * 5 / 38, height);
            g.fillStyle = 'white';
            g.fillText(list[i], rectX + width * 5 / 76, rectY + height / 2);
            rectX += width * 6 / 38;
        }    
    } else {     // 不带符号位的绘制 
        g.fillStyle = '#383838';
        g.fillRect(rectX, rectY, width * 5 / 35, height);
        g.fillStyle = 'white';
        g.fillText(list[i], rectX + width * 5 / 70, rectY + height / 2);
        rectX += width * 6 / 35;
    }      
}
g.restore();
}
