__ht__function(g, rect, comp, data, view) {
var x = rect.x;
var y = rect.y;
var width = rect.width;
var height = rect.height;

var borderColor = comp.getColor('borderColor');
var borderWidth = comp.getValue('borderWidth');
var background = comp.getColor('background');
var rowCount = comp.getValue('rowCount');
var columnCount = comp.getValue('columnCount');
var rowLineColor = comp.getColor('rowLineColor');
var columnLineColor = comp.getColor('columnLineColor');
var rowLineWidth = comp.getValue('rowLineWidth');
var columnLineWidth = comp.getValue('columnLineWidth');

// draw background
if (background) {
    g.fillStyle = background;
    g.beginPath();
    g.rect(x, y, width, height);
    g.fill();
}

// draw border
if (borderWidth) {
    ht.Default.drawBorder(g, borderColor, x, y, width, height, borderWidth);
}

x += borderWidth;
y += borderWidth;
width -= borderWidth * 2;
height -= borderWidth * 2;

var rowHeight = height / rowCount;
var columnWidth = width / columnCount;

// draw row lines
if (rowLineWidth && rowCount > 1) {
    g.beginPath();
    for (var i = 1; i < rowCount ; i++) {
        g.moveTo(x, y + rowHeight * i);
        g.lineTo(x + width, y + rowHeight * i);
    }
    g.strokeStyle = rowLineColor;
    g.lineWidth = rowLineWidth;
    g.stroke();
}

// draw column lines
if (columnLineWidth && columnCount > 1) {
    g.beginPath();
    for (var i = 1; i < columnCount ; i++) {
        g.moveTo(x + columnWidth * i, y);
        g.lineTo(x + columnWidth * i, y + height);
    }
    g.strokeStyle = columnLineColor;
    g.lineWidth = columnLineWidth;
    g.stroke();
}
}
