__ht__function onCancel(data) {
    var tools = new Tools();
    var dm = data.getDataModel();
    var inputButton = dm.getDataByTag('inputButton');
    var title = dm.getDataByTag('title');
    var distX = 350;
    data.s({ '2d.visible': false });
    tools.moveHorizontal([inputButton, title], distX);
}

a.onConfirm:
__ht__function onConfirm(data) {
    function createStartAnim(dataList, distX, finishFunc) {
        ht.Default.startAnim({
            duration: 500,
            easing: function(t) {
                return t * t;
            },
            finishFunc: finishFunc || null,
            action: function(v) {
                for (const item of dataList) {
                    item.setX(item.getX() + (distX - item.getX()) * v);
                }
            }
        });
    }
    var dm = data.getDataModel();
    var outputVal = data.getAttr('outputVal');
    var inputBtn = dm.getDataByTag('inputBtn');
    var title = dm.getDataByTag('title');
    inputBtn.setAttr('ht.text', outputVal);
    data.s({ '2d.visible': false });
    var distX = 350;
    createStartAnim([inputBtn, title], distX);
}

