s.onDown:
__ht__function(event, data, view) {
// 判断keypad是否隐藏
var dm = data.getDataModel();
var keypad = dm.getDataByTag('keypad');
var isVisible = keypad.s('2d.visible');
if (isVisible) return;

// 获取变量
var inputButton = dm.getDataByTag('inputButton');
var title = dm.getDataByTag('title');
var distX = 420;
var inputVal = inputButton.getAttr('ht.text') || '';

// 动画
moveHorizontal([inputButton, title], distX, function () {
    keypad.s({ '2d.visible': true });
    keypad.a({ outputVal: inputVal });
})
}

