clip:
__ht__function(g, width, height, data, view, image) {
if (!data) return;
var max = Math.min(width, height) / 2,
    clipRadius = data.a('clipRadius') || '0',
    rs = clipRadius.trim().split(/\s+/),
    lt, rt, rb, lb;
if (rs.length === 1) {
    lt = rt = rb = lb = rs[0] * 1;
}
else if (rs.length === 2) {
    lt = rb = rs[0] * 1;
    rt = lb = rs[1] * 1;
}
else if (rs.length === 3) {
    lt = rs[0] * 1;
    rt = lb = rs[1] * 1;
    rb = rs[2] * 1;
}
else {
    lt = rs[0] * 1;
    rt = rs[1] * 1;
    rb = rs[2] * 1;
    lb = rs[3] * 1;
}

rs = [lt, rt, lb, rb].map(function(r){
    return Math.min(r, max);
});


g.beginPath();
ht.Default.drawRoundRect(g, 0, 0, width, height, rs[0], rs[1], rs[2], rs[3]);
g.clip();
}
