clipPercentage.func:
__ht__function(data, view) {
if(data.a('status') === 1) {
    return (
        ht.Default.startAnim({
            duration: 2000,
            action: function (v, t) {
                if (data.s('clip.percentage') < 1) {
                    data.s('clip.percentage', data.s('clip.percentage') + 0.05)
                }
            }
        })
    )
}
if(data.a('status') !== 1) {
    return (
        ht.Default.startAnim({
            duration: 2000,
            action: function (v, t) {
                if (data.s('clip.percentage') > 0) {
                    data.s('clip.percentage', data.s('clip.percentage') - 0.05)
                }
            }
        })
    )
}
}

