background.func:
__ht__function(data, view) {
var status = data.a('status') || 1;
switch (status) {
	case 1:
		return 'rgb(77,255,0)';
	case 2:
		return '#F6A623';
	case 3:
		return '#D0011B';
	case 4:
		return 'rgb(181,181,181)';

}
}

text.func:
__ht__function(data, view) {
var status = data.a('status') || 1;
switch (status) {
	case 1:
		return '正常';
	case 2:
		return '告警';
	case 3:
		return '紧急告警';
	case 4:
		return '离线';

}
}
