__ht__function(data, gv, cache) {

    var option = {
        title : {
            text: 'iphone销量',
            subtext: '纯属虚构',
            x:'center',
            show:false
        },
        tooltip : {
            trigger: 'item',
            show:false
        },
        legend: {
            orient: 'vertical',
            x:'left',
            data:['iphone3'],
            show:false
        },
        dataRange: {
            min: 0,
            max: 2500,
            x: 'left',
            y: 'bottom',
            text:['高','低'],           // 文本，默认为数值文本
            calculable : true,
            show:false
        },
        roamController: {
            show: true,
            x: 'right',
            mapTypeControl: {
                'qingdao': true
            },
            show:false
