clip:
__ht__function(g, width, height, data, view, image) {
g.beginPath();
g.arc(width / 2, height /2, width / 2, 0, 2 * Math.PI);
g.clip();
}

waveRange.func:
__ht__function(data, view) {
var livePower = data.getAttr('livePower');
var designPower = data.getAttr('designPower');
if (livePower > designPower) return 1;
else {
    return livePower / designPower;
}
}
