renderHTML:
__ht__function(data, gv, cache) {

    var option = {
        color: ['rgb(222,122,35)', '#32D3EB'],
        tooltip : {
            trigger: 'axis',
            axisPointer: {
                type: 'line',
                label: {
                    backgroundColor: '#6a7985'
                }
            }
        },
        legend: {
            show: true,
            left: 'center',
            top: 0,
            textStyle: {
                color: '#fff'
            }
        },
        grid: {
            left: 0,
            right: 0,
            bottom: 0,
            top: '15%',
            containLabel: true
        },
        xAxis : [
            {
                type : 'category',
                boundaryGap : false,
                axisLine: {show: false},
                splitLine: {show: false},
                axisLabel: {
                    color: '#fff'
                }
            }
        ],
        yAxis : [
            {
                type : 'value',
                axisLine: {show: false},
                splitLine: {show: false},
                axisLabel: {
                    color: '#fff'
                }
            }
        ],
        series : [
            {
                type:'line',
                name: '风压',
            },{
                type:'line',
                name: '均顶温',
            }
        ]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data

    option.series[0].data = data.a('A_BP5S');
    option.series[1].data = data.a('A_TTOP3');
    option.xAxis[0].data = data.a('xAxisData');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;
}
