renderHTML:
__ht__function(data, gv, cache) {

    var option = {
        tooltip: {
            show: true
        },
        xAxis: {
            type: 'category',
            data: ['高炉', '平均', '最佳'],
            axisLabel: {
                color: 'rgb(255,255,255)'
            },
            axisTick: {show: false},
            axisLine: {
                lineStyle: {color: 'rgba(255,255,255,0.3)'}
            },
            splitLine: {
                lineStyle: {color: 'rgba(255,255,255,0.3)'}
            }
        },
        yAxis: {
            type: 'value',
            axisLabel: {
                color: 'rgb(255,255,255)'
            },
            axisTick: {show: false},
            axisLine: {
                lineStyle: {color: 'rgba(255,255,255,0.3)'}
            },
            splitLine: {
                lineStyle: {color: 'rgba(255,255,255,0.3)'}
            }
        },
        series: [{
            barCategoryGap:'50%',
            data: [120, 200, 150],
            type: 'bar',
            itemStyle: {
                normal: {
                    color: new echarts.graphic.LinearGradient(
                        0, 0, 1, 1,
                        [
                            {offset: 0, color: '#60ACFC'},
                            {offset: 1, color: 'rgb(1,100,143)'}
                        ]
                    )
                },
            }
        }]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function () {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // // update data
    // option.color = [data.a('lineColor1'), data.a('lineColor2')];
    // option.legend.data = [data.a('legendData1'), data.a('legendData2')];
    option.series[0].data = data.a('barData');
    option.series[0].itemStyle = {
        normal: {
            color: new echarts.graphic.LinearGradient(
                0, 0, 1, 1,
                [
                    {offset: 0, color: data.a('color2')},
                    {offset: 1, color: data.a('color1')}
                ]
            )
        }
    };
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;
}
